CREATE TABLE `users`( `id` INT(11) NOT NULL AUTO_INCREMENT,`user_code` VARCHAR(255),`name` VARCHAR(255),`address` VARCHAR(255),`phone` VARCHAR(255),`PID` VARCHAR(255),`tax_code` VARCHAR(255),`blank_account_number` VARCHAR(255),`blank_account_name` VARCHAR(255),`password` VARCHAR(255),`username` VARCHAR(255),`role` numeric(60) DEFAULT NULL, `updated_at` TIMESTAMP NULL, `created_at` TIMESTAMP NULL, `status` ENUM('0','1'), PRIMARY KEY (`id`));    
CREATE TABLE `bill`( `id` INT(11) NOT NULL AUTO_INCREMENT,`bill_type` VARCHAR(255),`user_id` numeric(60) DEFAULT NULL,`reason_note` VARCHAR(255),`original_docs` VARCHAR(255),`wh_id` numeric(60) DEFAULT NULL, `updated_at` TIMESTAMP NULL, `created_at` TIMESTAMP NULL, `status` ENUM('0','1'), PRIMARY KEY (`id`));